-- View structure for vw_orders
-- ----------------------------
DROP VIEW IF EXISTS `vw_orders`;
CREATE VIEW `vw_orders` AS
  SELECT
    `customer_order`.`ORDER_ID`       AS `ORDER_ID`,
    `customer_order`.`USER_ID`        AS `USER_ID`,
    `customer_order`.`KITCHEN_ID`     AS `KITCHEN_ID`,
    `customer_order`.`CHEF_ID`        AS `CHEF_ID`,
    `customer_order`.`RIDER_ID`       AS `RIDER_ID`,
    `tb_users`.`MOBILE`               AS `MOBILE`,
    `tb_users`.`SURNAME`              AS `SURNAME`,
    `tb_users`.`OTHER_NAMES`          AS `OTHER_NAMES`,
    `customer_order`.`ORDER_DATE`     AS `ORDER_DATE`,
    `customer_order`.`ORDER_STATUS`   AS `ORDER_STATUS`,
    `payment`.`PAYMENT_AMOUNT`        AS `PAYMENT_AMOUNT`,
    `payment`.`PAYMENT_NUMBER`        AS `PAYMENT_NUMBER`,
    `customer_order`.`NOTES`          AS `NOTES`,
    `customer_order`.`PAYMENT_METHOD` AS `PAYMENT_METHOD`,
    `customer_order`.`CREATED_AT`     AS `CREATED_AT`,
    `customer_order`.`UPDATED_AT`     AS `UPDATED_AT`,
    `payment`.`PAYMENT_DATE`          AS `PAYMENT_DATE`,
    `location`.`LOCATION_ID`          AS `LOCATION_ID`,
    `location`.`LOCATION_NAME`        AS `LOCATION_NAME`,
    `location`.`ADDRESS`              AS `ADDRESS`,
    `city`.`CITY_NAME`                AS `CITY_NAME`,
    `city`.`CITY_ID`                  AS `CITY_ID`,
    `country`.`COUNRY_ID`             AS `COUNRY_ID`,
    `country`.`COUNTRY_NAME`          AS `COUNTRY_NAME`
  FROM (((((`customer_order`
    JOIN `tb_users` ON ((`customer_order`.`USER_ID` = `tb_users`.`USER_ID`))) LEFT JOIN `payment`
      ON ((`payment`.`ORDER_ID` = `customer_order`.`ORDER_ID`))) JOIN `location`
      ON ((`customer_order`.`LOCATION_ID` = `location`.`LOCATION_ID`))) JOIN `city`
      ON ((`location`.`CITY_ID` = `city`.`CITY_ID`))) JOIN `country`
      ON ((`city`.`COUNTRY_ID` = `country`.`COUNRY_ID`)));

-- ----------------------------
-- View structure for vw_order_items
-- ----------------------------
DROP VIEW IF EXISTS `vw_order_items`;
CREATE VIEW `vw_order_items` AS
  SELECT
    customer_order.ORDER_ID,
    customer_order_item.QUANTITY,
    customer_order_item.PRICE,
    customer_order_item.SUBTOTAL,
    menu_item_type.ITEM_TYPE_SIZE,
    menu_item.MENU_ITEM_NAME,
    menu_category.MENU_CAT_NAME,
    menu_category.MENU_CAT_IMAGE,
    menu_item.MENU_ITEM_IMAGE,
    customer_order.USER_ID
  FROM
    customer_order
    INNER JOIN customer_order_item ON customer_order_item.ORDER_ID = customer_order.ORDER_ID
    INNER JOIN menu_item_type ON customer_order_item.ITEM_TYPE_ID = menu_item_type.ITEM_TYPE_ID
    INNER JOIN menu_item ON menu_item_type.MENU_ITEM_ID = menu_item.MENU_ITEM_ID
    INNER JOIN menu_category ON menu_item.MENU_CAT_ID = menu_category.MENU_CAT_ID;
